-- Create "users" table
CREATE TABLE "users" ("id" character varying NOT NULL, "first_name" character varying NOT NULL DEFAULT 'unknown', "company_name" character varying NOT NULL DEFAULT 'unknown', "email" character varying NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "last_login" timestamptz NOT NULL, PRIMARY KEY ("id"));
-- Create index "users_email_key" to table: "users"
CREATE UNIQUE INDEX "users_email_key" ON "users" ("email");
-- Create "jobs" table
CREATE TABLE "jobs" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "title" character varying NOT NULL, "created_at" timestamptz NOT NULL, "location" character varying NULL, "description" character varying NOT NULL, "skills" jsonb NULL, "experience_level" character varying NULL, "hourly" boolean NOT NULL, "fixed" boolean NOT NULL, "hourly_rate" jsonb NULL, "fixed_rate" numeric NULL, "average_uprank_score" double precision NULL, "max_uprank_score" double precision NULL, "min_uprank_score" double precision NULL, "user_jobs" character varying NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "jobs_users_jobs" FOREIGN KEY ("user_jobs") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "freelancers" table
CREATE TABLE "freelancers" ("id" uuid NOT NULL, "url" character varying NOT NULL, "name" character varying NOT NULL, "title" character varying NOT NULL, "description" character varying NOT NULL, "city" character varying NOT NULL, "country" character varying NOT NULL, "timezone" character varying NOT NULL, "cv" text NOT NULL, "ai_reccomended" boolean NOT NULL, "fixed_charge_amount" bigint NULL, "fixed_charge_currency" character varying NOT NULL, "hourly_charge_amount" bigint NULL, "hourly_charge_currency" character varying NOT NULL, "invited" boolean NOT NULL, "photo_url" character varying NOT NULL, "recent_hours" bigint NOT NULL, "total_hours" bigint NOT NULL, "total_portfolio_items" bigint NOT NULL, "total_portfolio_v2_items" bigint NOT NULL, "upwork_total_feedback" numeric NOT NULL, "upwork_recent_feedback" numeric NOT NULL, "upwork_top_rated_status" boolean NOT NULL, "upwork_top_rated_plus_status" boolean NOT NULL, "upwork_sponsored" boolean NOT NULL, "upwork_job_success_score" numeric NOT NULL, "upwork_reccomended" boolean NOT NULL, "skills" jsonb NOT NULL, "average_recent_earnings" numeric NOT NULL, "combined_average_recent_earnings" numeric NOT NULL, "combined_recent_earnings" numeric NOT NULL, "combined_total_earnings" numeric NOT NULL, "combined_total_revenue" numeric NOT NULL, "recent_earnings" numeric NOT NULL, "total_revenue" numeric NOT NULL, "uprank_score" bigint NULL DEFAULT 0, "uprank_updated_at" timestamptz NOT NULL, "uprank_reccomended" boolean NULL DEFAULT false, "uprank_reccomended_reasons" character varying NULL, "uprank_not_enough_data" boolean NULL DEFAULT false, "job_freelancers" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "freelancers_jobs_freelancers" FOREIGN KEY ("job_freelancers") REFERENCES "jobs" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "freelancers_url_key" to table: "freelancers"
CREATE UNIQUE INDEX "freelancers_url_key" ON "freelancers" ("url");
-- Create "attachment_refs" table
CREATE TABLE "attachment_refs" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "url" character varying NOT NULL, "freelancer_attachments" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "attachment_refs_freelancers_attachments" FOREIGN KEY ("freelancer_attachments") REFERENCES "freelancers" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "work_histories" table
CREATE TABLE "work_histories" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "title" character varying NOT NULL, "client_feedback" character varying NOT NULL, "overall_rating" numeric NOT NULL, "fixed_charge_amount" bigint NULL, "fixed_charge_currency" character varying NULL, "hourly_charge_amount" bigint NULL, "hourly_charge_currency" character varying NULL, "start_date" timestamptz NOT NULL, "end_date" timestamptz NULL, "job_description" character varying NOT NULL, "total_proposals" bigint NOT NULL, "number_of_interviews" bigint NOT NULL, "skills" jsonb NOT NULL, "client_rating" numeric NOT NULL, "client_review_count" bigint NOT NULL, "client_country" character varying NOT NULL, "client_total_jobs_posted" bigint NOT NULL, "client_total_spend" numeric NOT NULL, "client_total_hires" bigint NULL, "client_total_paid_hours" bigint NULL, "client_average_hourly_rate_paid" numeric NULL, "client_company_category" character varying NULL, "client_company_size" character varying NULL, "freelancer_work_histories" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "work_histories_freelancers_work_histories" FOREIGN KEY ("freelancer_work_histories") REFERENCES "freelancers" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
